const CODE_TYPE = {
    SUCCESS: 'success', GO_TO_LOGIN: 'goToLogin', COMMON_ERROR: 'commonError'
};
// 返回码对照表，后台定义见注释，message为对应的前端本地化提示
const CODE = {
    0: {type: CODE_TYPE.SUCCESS},// 返回成功
    301: {type: CODE_TYPE.COMMON_ERROR, message: '到达每日发送上限'},
    302: {type: CODE_TYPE.COMMON_ERROR, message: (res) => `请${res.Desc}秒后再次获取验证码！`},
    50000: {type: CODE_TYPE.COMMON_ERROR},// 内部错误
    50001: {type: CODE_TYPE.COMMON_ERROR},// 输入参数错误
    50002: {type: CODE_TYPE.COMMON_ERROR},// uid缺失
    50003: {type: CODE_TYPE.COMMON_ERROR},// uuid缺失
    50004: {type: CODE_TYPE.COMMON_ERROR},// 接口废弃
    50005: {type: CODE_TYPE.COMMON_ERROR, message: '页面停留太久，已有更新数据，请刷新页面后重新编辑'},// 参数过期
    50006: {type: CODE_TYPE.COMMON_ERROR},// 缺少参数
    // ASManager错误
    990001: {type: CODE_TYPE.COMMON_ERROR},// 服务器处理超时
    990002: {type: CODE_TYPE.GO_TO_LOGIN},// Token验证失败
    990003: {type: CODE_TYPE.GO_TO_LOGIN},// Sign验证失败
    990004: {type: CODE_TYPE.COMMON_ERROR},// 未找到服务
    990005: {type: CODE_TYPE.COMMON_ERROR},// 内部错误（grpc）
    990006: {type: CODE_TYPE.COMMON_ERROR},// 调用超时
    990007: {type: CODE_TYPE.COMMON_ERROR},// 超过重试次数
    990008: {type: CODE_TYPE.GO_TO_LOGIN},// Token过期
};

const CODE_EVENT = {
    [CODE_TYPE.SUCCESS]: {},
    [CODE_TYPE.GO_TO_LOGIN]: {},
    [CODE_TYPE.COMMON_ERROR]: {},
};

export {CODE_TYPE, CODE_EVENT, CODE};